#requires -Version 3.0
  <#PSScriptInfo

      .VERSION 0.2

      .GUID 868d6a66-99c3-4388-85aa-5026a9406215

      .AUTHOR 

      .COMPANYNAME

      .COPYRIGHT

      .TAGS Helpdesk Troubleshooting Workstations

      .LICENSEURI

      .PROJECTURI https://github.com/PsCustomObject/PowerTool

      .ICONURI

      .EXTERNALMODULEDEPENDENCIES 

      .REQUIREDSCRIPTS

      .EXTERNALSCRIPTDEPENDENCIES

      .RELEASENOTES

      .PRIVATEDATA

  #>
  <#
    .SYNOPSIS
    Opens a GUI menu. Helpdesk tool for troubleshooting remote workstations

    .DESCRIPTION
    Helpdesk tool for troubleshooting remote workstations.  This is only the shell and doesn't actually have any working scripts.

    .EXAMPLE
    PowerTool.ps1

    .NOTES

    .LINK
    https://github.com/PsCustomObject/PowerTool
    The first link is opened by Get-Help -Online 

    .INPUTS
    List of input types that are accepted by this function.

    .OUTPUTS
    List of output types produced by this function.
  #>



# ================================ [Assemblies & Variables]
Begin {
  ## Assemblies
  Add-Type -AssemblyName System.Windows.Forms
  Add-Type -AssemblyName System.Drawing
  [System.Windows.Forms.Application]::EnableVisualStyles()
  Add-Type -AssemblyName Microsoft.VisualBasic

  ## Form Variables
  $backgroundColour = 'POWDERBLUE'
  $buttonColour = 'White'
  $font = 'Consolas,11'
  $PtLine = '-' * 109
  Set-Location -Path $PSScriptRoot

  # Dot source functions file
  . .\Functions\PowerTool-Function.ps1
}
# ================================ [Form GUI]
Process {
  ### Main Form
  $PowerTool = New-Object -TypeName system.Windows.Forms.Form
  $PowerTool.MinimumSize = New-Object -TypeName System.Drawing.Size -ArgumentList (770, 380)
  $PowerTool.Font = $font
  $PowerTool.Width = [int]960
  $PowerTool.Height = [int]500
  $PowerTool.AutoSize = $false
  $PowerTool.text = 'PowerTool'
  $PowerTool.BackColor = $backgroundColour
  $PowerTool.StartPosition = 'CenterScreen'
  $PowerTool.FormBorderStyle = 'FixedDialog'
  $PowerTool.MaximizeBox = $false
  $PowerTool.ControlBox = $True
  $PowerTool.MinimizeBox = $True
  $PowerTool.mainMenuStrip = $mainMenu
  $PowerTool.KeyPreview = $True


  # Logging Textbox
  $txbLogging = New-Object -TypeName 'System.Windows.Forms.RichTextBox'
  $txbLogging.Font = $font
  $txbLogging.Location = '20, 85'
  $txbLogging.Size = '900, 325'
  $txbLogging.Text = ("{0}`r`n{1}`r`n" -f 'A live log of actions will be displayed here', $PtLine)
  $txbLogging.Multiline = $True
  $txbLogging.ReadOnly = $True
  $txbLogging.TabIndex = 5
  $txbLogging.add_TextChanged($txbLogging_textchanged)
  $PowerTool.Controls.Add($txbLogging)
  # Scroll to bottom of textbox when log updated
  $txbLogging_textchanged = {
    $txbLogging.SelectionStart = $txbLogging.Text.Length
    $txbLogging.ScrollToCaret()
  }

  # Connect Button
  $btnConnect = New-Object -TypeName system.Windows.Forms.Button
  $btnConnect.text = 'Connect'
  $btnConnect.width = 200
  $btnConnect.height = 30
  $btnConnect.location = New-Object -TypeName System.Drawing.Point -ArgumentList (722, 43)
  $btnConnect.Font = 'Microsoft Sans Serif,10'
  $btnConnect.BackColor = $buttonColour
  $btnConnect.TabIndex = 2
  $PowerTool.Controls.Add($btnConnect)

  # Hostname Label
  $lblHostname = New-Object -TypeName system.Windows.Forms.Label
  $lblHostname.text = 'Enter Computer Name or IP Address:'
  $lblHostname.AutoSize = $True
  $lblHostname.location = New-Object -TypeName System.Drawing.Point -ArgumentList (20, 50)
  $lblHostname.Font = 'Microsoft Sans Serif,10'
  $PowerTool.Controls.Add($lblHostname)

  # Hostname Textbox
  $txbHostname = New-Object -TypeName system.Windows.Forms.TextBox
  $txbHostname.multiline = $false
  $txbHostname.width = 300
  $txbHostname.height = 30
  $txbHostname.CharacterCasing = 'Upper'
  $txbHostname.location = New-Object -TypeName System.Drawing.Point -ArgumentList (253, 47)
  $txbHostname.Font = 'Microsoft Sans Serif,10'
  $txbHostname.TabIndex = 0
  $PowerTool.Controls.Add($txbHostname)

  # Clear Button
  $btnClearLogs = New-Object -TypeName system.Windows.Forms.Button
  $btnClearLogs.text = 'Clear Logs'
  $btnClearLogs.width = 100
  $btnClearLogs.height = 30
  $btnClearLogs.location = New-Object -TypeName System.Drawing.Point -ArgumentList (822, 420)
  $btnClearLogs.Font = 'Microsoft Sans Serif,10'
  $btnClearLogs.BackColor = $buttonColour
  $btnClearLogs.TabIndex = 4
  $PowerTool.Controls.Add($btnClearLogs)

  # Copy Button
  $btnCopyLogs = New-Object -TypeName system.Windows.Forms.Button
  $btnCopyLogs.text = 'Copy Logs'
  $btnCopyLogs.width = 100
  $btnCopyLogs.height = 30
  $btnCopyLogs.location = New-Object -TypeName System.Drawing.Point -ArgumentList (722, 420)
  $btnCopyLogs.Font = 'Microsoft Sans Serif,10'
  $btnCopyLogs.BackColor = $buttonColour
  $btnCopyLogs.TabIndex = 3
  $PowerTool.Controls.Add($btnCopyLogs)

  ### Main Menu Strip
  $mainMenu = New-Object -TypeName 'System.Windows.Forms.MenuStrip'
  $mainMenu.Font = 'Trebuchet MS, 9pt'
  $mainMenu.Location = '0, 0'
  $mainMenu.Size = '1170, 26'
  $mainMenu.Text = 'menustrip1'
  $PowerTool.Controls.Add($mainMenu)

  ## Tools Dropdown
  $toolsDropdown = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $toolsDropdown.Size = '109, 22'
  $toolsDropdown.Text = 'Admin Tools'
  $mainMenu.Items.Add($toolsDropdown)

  # File Explorer
  $tools_Explorer = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $tools_Explorer.Size = '109, 22'
  $tools_Explorer.Text = 'File Explorer'
  $toolsDropdown.DropDownItems.Add($tools_Explorer)

  # Services.msc
  $tools_Services = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $tools_Services.Size = '109, 22'
  $tools_Services.Text = 'Services'
  $toolsDropdown.DropDownItems.Add($tools_Services)

  # Regedit
  $tools_Regedit = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $tools_Regedit.Size = '109, 22'
  $tools_Regedit.Text = 'Regedit'
  $toolsDropdown.DropDownItems.Add($tools_Regedit)

  # PowerShell
  $tools_PowerShell = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $tools_PowerShell.Size = '109, 22'
  $tools_PowerShell.Text = 'PowerShell'
  $toolsDropdown.DropDownItems.Add($tools_PowerShell)

  # CMD
  $tools_CMD = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $tools_CMD.Size = '109, 22'
  $tools_CMD.Text = 'CMD'
  $toolsDropdown.DropDownItems.Add($tools_CMD)

  # Device Manager
  $tools_DeviceManager = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $tools_DeviceManager.Size = '109, 22'
  $tools_DeviceManager.Text = 'Device Manager'
  $toolsDropdown.DropDownItems.Add($tools_DeviceManager)

  # Event Viewer
  $tools_EventViewer = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $tools_EventViewer.Size = '109, 22'
  $tools_EventViewer.Text = 'Event Viewer'
  $toolsDropdown.DropDownItems.Add($tools_EventViewer)

  # Advanced System Settings
  $tools_AdvancedSettings = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $tools_AdvancedSettings.Size = '109, 22'
  $tools_AdvancedSettings.Text = 'Advanced System Settings'
  $toolsDropdown.DropDownItems.Add($tools_AdvancedSettings)

  # Seperator
  $tools_Seperator = New-Object -TypeName 'System.Windows.Forms.ToolStripSeparator'
  $tools_Seperator.Size = '109, 22'
  $toolsDropdown.DropDownItems.Add($tools_Seperator)

  # Remote - Explorer
  $tools_remote_Explorer = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $tools_remote_Explorer.Size = '109, 22'
  $tools_remote_Explorer.Text = 'Remote Explorer'
  $tools_remote_Explorer.Enabled = $false
  $toolsDropdown.DropDownItems.Add($tools_remote_Explorer)

  # Remote - Services
  $tools_remote_Services = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $tools_remote_Services.Size = '109, 22'
  $tools_remote_Services.Text = 'Remote Services'
  $tools_remote_Services.Enabled = $false
  $toolsDropdown.DropDownItems.Add($tools_remote_Services)

  # Remote - Ping
  $tools_remote_Ping = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $tools_remote_Ping.Size = '109, 22'
  $tools_remote_Ping.Text = 'Remote Ping'
  $tools_remote_Ping.Enabled = $false
  $toolsDropdown.DropDownItems.Add($tools_remote_Ping)

  ## Account Information
  $accountInformationDropdown = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $accountInformationDropdown.Size = '109, 22'
  $accountInformationDropdown.Text = 'Account Information'
  $mainMenu.Items.Add($accountInformationDropdown)

  # Locked Account
  $account_LockedOut = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $account_LockedOut.Size = '109, 22'
  $account_LockedOut.Text = 'Check Locked Account'
  $accountInformationDropdown.DropDownItems.Add($account_LockedOut)

  # Expired Account
  $account_Expired = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $account_Expired.Size = '109, 22'
  $account_Expired.Text = 'Check Expired Account'
  $accountInformationDropdown.DropDownItems.Add($account_Expired)

  ## Computer Information
  $computerInformationDropdown = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $computerInformationDropdown.Visible = $false
  $computerInformationDropdown.Size = '109, 22'
  $computerInformationDropdown.Text = 'Computer Information'
  $mainMenu.Items.Add($computerInformationDropdown)

  # Asset Tag & Serial
  $computer_AssetSerial = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $computer_AssetSerial.Size = '109, 22'
  $computer_AssetSerial.Text = 'Asset and Serial'
  $computerInformationDropdown.DropDownItems.Add($computer_AssetSerial)

  # Device Model
  $computer_Model = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $computer_Model.Size = '109, 22'
  $computer_Model.Text = 'Model and Manufacturer'
  $computerInformationDropdown.DropDownItems.Add($computer_Model)

  # Operating System
  $computer_OperatingSystem = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $computer_OperatingSystem.Size = '109, 22'
  $computer_OperatingSystem.Text = 'OS Version'
  $computerInformationDropdown.DropDownItems.Add($computer_OperatingSystem)

  # Brower Information
  $computer_BrowserInformation = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $computer_BrowserInformation.Size = '109, 22'
  $computer_BrowserInformation.Text = 'Browser Information'
  $computerInformationDropdown.DropDownItems.Add($computer_BrowserInformation)

  # IP Address
  $computer_IPAddress = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $computer_IPAddress.Size = '109, 22'
  $computer_IPAddress.Text = 'IP Address'
  $computerInformationDropdown.DropDownItems.Add($computer_IPAddress)

  # Seperator One
  $computer_seperator_one = New-Object -TypeName 'System.Windows.Forms.ToolStripSeparator'
  $computer_seperator_one.Size = '109, 22'
  $computerInformationDropdown.DropDownItems.Add($computer_seperator_one)

  # Machine Uptime
  $computer_MachineUptime = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $computer_MachineUptime.Size = '109, 22'
  $computer_MachineUptime.Text = 'Machine Uptime'
  $computerInformationDropdown.DropDownItems.Add($computer_MachineUptime)

  # Last Reboot
  $computer_LastReboot = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $computer_LastReboot.Size = '109, 22'
  $computer_LastReboot.Text = 'Last Reboot'
  $computerInformationDropdown.DropDownItems.Add($computer_LastReboot)

  # Build Date
  $computer_BuildDate = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $computer_BuildDate.Size = '109, 22'
  $computer_BuildDate.Text = 'Build Date'
  $computerInformationDropdown.DropDownItems.Add($computer_BuildDate)

  # Seperator Two
  $computer_seperator_two = New-Object -TypeName 'System.Windows.Forms.ToolStripSeparator'
  $computer_seperator_two.Size = '109, 22'
  $computerInformationDropdown.DropDownItems.Add($computer_seperator_two)

  # CPU Usage
  $computer_CPU = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $computer_CPU.Size = '109, 22'
  $computer_CPU.Text = 'Current CPU Usage'
  $computerInformationDropdown.DropDownItems.Add($computer_CPU)

  # Power Settings
  $computer_PowerSettings = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $computer_PowerSettings.Size = '109, 22'
  $computer_PowerSettings.Text = 'Power settings'
  $computerInformationDropdown.DropDownItems.Add($computer_PowerSettings)

  # Disk & Memory Information
  $computer_DiskInformation = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $computer_DiskInformation.Size = '109, 22'
  $computer_DiskInformation.Text = 'Disk and Memory Info'
  $computerInformationDropdown.DropDownItems.Add($computer_DiskInformation)

  ## Scripts & Fixes
  $fixesDropdown = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $fixesDropdown.Visible = $false
  $fixesDropdown.Size = '109, 22'
  $fixesDropdown.Text = 'Scripts'
  $mainMenu.Items.Add($fixesDropdown)

  # Scripts Dropdown - Dynamically generated
  foreach($file in (Get-ChildItem -Path '.\Scripts' ))
  {
    # To generate list items, add your script files to .\Scripts

    $dropdownName = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
    $dropdownName.Size = '109, 22'
    $dropdownName.Text = $file.Name
    $fixesDropdown.DropDownItems.Add($dropdownName)

    $dropdownName.Add_Click({
        # Invoke your script here
        Edit-PTlog -Write -logString "$this has been invoked on $hostname"
        
        & ".\Scripts\$this"

    })
  }

  ## Applications
  $applicationsDropdown = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $applicationsDropdown.Visible = $false
  $applicationsDropdown.Size = '109, 22'
  $applicationsDropdown.Text = 'Applications'
  $mainMenu.Items.Add($applicationsDropdown)

  # Blank Dropdown
  $applications_Template = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $applications_Template.Size = '109, 22'
  $applications_Template.Text = 'Blank Dropdown'
  $applicationsDropdown.DropDownItems.Add($applications_Template)

  ## App-V
  $appvDropdown = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $appvDropdown.Visible = $false
  $appvDropdown.Size = '109, 22'
  $appvDropdown.Text = 'App-V'
  $mainMenu.Items.Add($appvDropdown)

  # Blank Dropdown
  $appv_Template = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $appv_Template.Size = '109, 22'
  $appv_Template.Text = 'Blank Dropdown'
  $appvDropdown.DropDownItems.Add($appv_Template)

  ## Service Dropdown
  $servicesDropdown = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $servicesDropdown.Visible = $false
  $servicesDropdown.Size = '109, 22'
  $servicesDropdown.Text = 'Services'
  $mainMenu.Items.Add($servicesDropdown)

  # All Services
  $services_AllServices = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $services_AllServices.Size = '109, 22'
  $services_AllServices.Text = 'View All Services'
  $servicesDropdown.DropDownItems.Add($services_AllServices)

  #Running Services
  $services_RunningServices = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $services_RunningServices.Size = '109, 22'
  $services_RunningServices.Text = 'View Running Services'
  $servicesDropdown.DropDownItems.Add($services_RunningServices)

  # Stopped Services
  $services_StoppedServices = New-Object -TypeName 'System.Windows.Forms.ToolStripMenuItem'
  $services_StoppedServices.Size = '109, 22'
  $services_StoppedServices.Text = 'View Stopped Services'
  $servicesDropdown.DropDownItems.Add($services_StoppedServices)

  ### Menu Icons
  $toolsDropdown.Image = [System.Convert]::FromBase64String('')
  $computerInformationDropdown.Image = [System.Convert]::FromBase64String('')
  $applicationsDropdown.Image = [System.Convert]::FromBase64String('')
  $fixesDropdown.Image = [System.Convert]::FromBase64String('')
  $servicesDropdown.Image = [System.Convert]::FromBase64String('')
  $accountInformationDropdown.Image = [System.Convert]::FromBase64String('')
  $appvDropdown.Image = [System.Convert]::FromBase64String('')

  # ================================ [Button Events]

  $PowerTool.Add_KeyDown({
      # Emulate key press when entered key pressed

      if ($_.KeyCode -eq 'Return') 
      {
        Connect-PTworkstation

        PT-LineSpacer
      }
  })

  $btnConnect.Add_Click({
      Connect-PTworkstation

      PT-LineSpacer
  })

  $btnCopyLogs.Add_Click({
      Edit-PTlog -Copy
  })

  $btnClearLogs.Add_Click({
      Edit-PTlog -Clear
  })

  $tools_Explorer.Add_Click({
      Start-AdminTool -Tool Explorer
  })

  $tools_Services.Add_Click({
      Start-AdminTool -Tool Services
  })

  $tools_Regedit.Add_Click({
      Start-AdminTool -Tool Regedit
  })

  $tools_PowerShell.Add_Click({
      Start-AdminTool -Tool PowerShell
  })

  $tools_CMD.Add_Click({
      Start-AdminTool -Tool CMD
  })

  $tools_DeviceManager.Add_Click({
      Start-AdminTool -Tool DeviceManager
  })

  $tools_EventViewer.Add_Click({
      Start-AdminTool -Tool EventViewer
  })

  $tools_AdvancedSettings.Add_Click({
      Start-AdminTool -Tool SystemSettings
  })

  $tools_remote_Explorer.Add_Click({
      PT-RemoteExplorer
  })

  $tools_remote_Services.Add_Click({
      PT-RemoteServices
  })

  $tools_remote_Ping.Add_Click({
      Start-AdminTool -Tool RemotePing
  })

  ### Show Form
  [void]$PowerTool.ShowDialog() 
}

End{}
